import React, { useState } from 'react';
import { View, StyleSheet, ImageBackground, Text } from 'react-native';
import MainList from './mainList';
import { StatusBar } from 'expo-status-bar';

const image = { uri: 'https://c1.wallpaperflare.com/preview/945/664/948/cosmetics-tools-lipstick-accessories.jpg' };

export default function Home({ navigation }) {
  const [products, setProducts] = useState([
    {
      Title: "The Lion King",
      Year: "2019",
      Runtime: "118 min",
      Poster: "https://m.media-amazon.com/images/M/MV5BMjIwMjE1Nzc4NV5BMl5BanBnXkFtZTgwNDg4OTA1NzM@._V1_SX300.jpg"
    },
    {
      "Title": "Mowgli: Legend of the Jungle",
      "Year": "2018",
      "Runtime": "104 min",
      "Poster": "https://m.media-amazon.com/images/M/MV5BMjMzODc2NzU5MV5BMl5BanBnXkFtZTgwNTMwMTE3NjM@._V1_SX300.jpg"
    },
    {
      "Title": "Doctor Strange",
      "Year": "2016",
      "Runtime": "115 min",
      "Poster": "https://m.media-amazon.com/images/M/MV5BNjgwNzAzNjk1Nl5BMl5BanBnXkFtZTgwMzQ2NjI1OTE@._V1_SX300.jpg"
    },
    {
      "Title": "John Wick",
      "Year": "2014",
      "Runtime": "101 min",
      "Poster": "https://m.media-amazon.com/images/M/MV5BMTU2NjA1ODgzMF5BMl5BanBnXkFtZTgwMTM2MTI4MjE@._V1_SX300.jpg"
    },
    {
      "Title": "The Notebook",
      "Year": "2004",
      "Runtime": "123 min"
    }
  ]);

  return (
    <View style={styles.container}>
      <ImageBackground source={image} resizeMode="cover" style={styles.image}>
        <View style={styles.content}>
          <Text style={styles.title}>Makeup App</Text>
          <MainList PRODUCTS={products} />
        </View>
        <StatusBar style="auto" />
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width:'100%'
  },
  image: {
    flex: 1,

  },
  content: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
    textAlign: 'center',
    paddingTop: 30,
    paddingLeft: 10,
  },
});
